mapper.select:
SELECT * from managerinfo

	 and name=#{name} 
	 and type=#{type}

SELECT count(*) from managerinfo

	 and id=#{id}
	 and password=#{password}
	
	 and realname=#{realname}
	
	 and identity=#{identity}
	and tell=#{tell}
	 and name=#{name} 
	 and type=#{type}


select
*
from managerinfo

	
		and id=#{managerinfo.id}
	
	
		and name=#{managerinfo.name}
	
	
		and password=#{managerinfo.password}
	
	
		and realname=#{managerinfo.realname}
	
	
		and identity=#{managerinfo.identity}
	
	
		and tell=#{managerinfo.tell}
	
	
		and type=#{managerinfo.type}
	
	
		and extended1=#{managerinfo.extended1}
	
	
		and extended2=#{managerinfo.extended2}
	
	
		and extended3=#{managerinfo.extended3}
	
	
		and extended4=#{managerinfo.extended4}
	
	
		and extended5=#{managerinfo.extended5}
	
	
		and extended6=#{managerinfo.extended6}
	
	
		and extended7=#{managerinfo.extended7}
	
	
		and extended8=#{managerinfo.extended8}
	
	
		and extended9=#{managerinfo.extended9}
	
	
		and extended10=#{managerinfo.extended10}
	
	
		and extended11=#{managerinfo.extended11}
	
	
		and extended12=#{managerinfo.extended12}
	
	
		and extended13=#{managerinfo.extended13}
	
	
		and extended14=#{managerinfo.extended14}
	
	
		and extended15=#{managerinfo.extended15}
	
	
		and extended16=#{managerinfo.extended16}
	
	
		and extended17=#{managerinfo.extended17}
	
	
		and extended18=#{managerinfo.extended18}
	
	
		and extended19=#{managerinfo.extended19}
	
	
		and extended20=#{managerinfo.extended20}
	

limit #{page},#{max}
